from itertools import *
class Solution:
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if not words:
            return []
        substring = []
        for i in permutations(words,len(words)):
            substring.append(''.join(i))
        print(substring)
        ind = []
        for j in substring:
            temp = 0
            while j in s[temp:]:
                ind.append(s.index(j,temp,len(s)))
                temp = s.index(j,temp,len(s)) + 1
        return list(set(ind))

#the same lenth
class Solution1:
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if not words:
            return []
        l = len(words[0])
        d = {}
        for w in words:
            if w in d:
                d[w] += 1
            else:
                d[w] = 1
        ans = []
        for k in range(l):
            subd = {}
            count = 0
            left = k
            for i in range(k,len(s)-l+1,l):
                cword = s[i:i+l]
                if cword in words:
                    if cword in subd:
                        subd[cword] += 1
                    else:
                        subd[cword] = 1
                    count += 1
                    while subd[cword] > d[cword]:
                        subd[s[left:left+l]] -= 1
                        count -= 1
                        left += l
                    if count == len(words):
                        ans.append(left)
                else:
                    count = 0
                    subd = {}
                    left = i + l
        return ans



